    private javax.swing.JTextField messageBox;
    private javax.swing.JButton sendButton;
    // End of variables declaration//GEN-END:variables
    private Socket client;
    private ObjectOutputStream serverOutput;
    private ObjectInputStream serverInput;
    private final String serverIP = "127.0.0.1"; //Loopback address

    public void startClient() {
        try {
            while (true) {
                connectToServer();
                chat();
            }
        } catch (EOFException eofE) {
            printToGUI("Client terminated the connection");
        } catch (IOException ioE) {
            printToGUI("Server has terminated the connection");
        } finally {
            closeConnection();
        }
    }

    public void connectToServer() throws IOException {
        messageBox.setEditable(false);
        printToGUI("Attempting to connect to the server...");
        client = new Socket(serverIP, Server.PORT);
        printToGUI("Now connected to " + client.getInetAddress().getHostName() + "\n");
        messageBox.setEditable(true);

        serverOutput = new ObjectOutputStream(client.getOutputStream());
        serverOutput.flush();
        serverInput = new ObjectInputStream(client.getInputStream());
    }

    public void chat() throws IOException {
        String message = "";
        while (!message.equalsIgnoreCase("Server: exit")) {
            try {
                message = (String) serverInput.readObject();
                printToGUI(message);
            } catch (ClassNotFoundException ex) {
                System.out.println("Object not found");
            }
        }
    }

    public void closeConnection() {
        try {
            serverInput.close();
            serverOutput.close();
            client.close();
        } catch (IOException ex) {
            System.out.println("Error closing connection");
        }
    }

    private void sendMessage(String message) {
        try {
            message = "Client: " + message;
            serverOutput.writeObject(message);
            serverOutput.flush();
            printToGUI(message);
        } catch (IOException ex) {
            printToGUI("Error sending message, connection is not established");
        }
    }

    private void printToGUI(String message) {
        java.awt.EventQueue.invokeLater(new Runnable() {
            public void run() {
                String timeStamp = new SimpleDateFormat("hh:mm").format(new Date());
                clientChatWindow.append("\n" + timeStamp + " " + message);
            }
        });
    }

